val i = 10

// train a batch model over the first i weeks and save it to disk
BatchLearn(Array.range(0, i).map(inputPath + _), modelPath+i)
// read the model and evaluate it as-is
OnlineEvaluate(inputPath+i, modelPath+i, outputPathBatch+i)
// read the model and evaluate it, while also training it in an online fashion (batch then online)
OnlineLearnAndEvaluate(inputPath+i, modelPath+i, outputPathBatchOnline+i)

for(i <- 10 to 15){
    BatchLearn(Array.range(0, i).map(inputPath + _), modelPath+i)
    OnlineEvaluate(inputPath+i, modelPath+i, outputPathBatch+i)
    OnlineLearnAndEvaluate(inputPath+i, modelPath+i, outputPathBatchOnline+i)
}
