psOfflineMF(lastFM,
          numFactors = 20,
          learningRate = 0.1,
          minRange = -0.001,
          maxRange = 0.001,
          negativeSampleRate = 9,
          iterations = 2,
          pullLimit = 2000,
          workerParallelism = 4,
          psParallelism = 4,
          iterationWaitTime = 20000)
             .addSink(new RichSinkFunction[Either[(UserId, Vector), (ItemId, Vector)]] {

              val userVectors = new mutable.HashMap[UserId, Array[Double]]
              val itemVectors = new mutable.HashMap[ItemId, Array[Double]]

              override def invoke(value: Either[(UserId, Vector), (ItemId, Vector)]): Unit = {
                value match {
                  case Left((userId, vec)) =>
                    userVectors.update(userId, vec)
                  case Right((itemId, vec)) =>
                    itemVectors.update(itemId, vec)
                }
              }

              override def close(): Unit = {
                val modelFile = new java.io.PrintWriter(new java.io.File("model"))
                val userVectorFile = new java.io.PrintWriter(new java.io.File(userVectorFileName))

                for((k,v) <- userVectors){
                    modelFile.write("u;" + k)
                  for(value <- v){
                    userVectorFile.write(k + ";" + value + '\n')
                    modelFile.write(";" + value)
                  }
                  modelFile.write("\n")
                }
                userVectorFile.close()

                val itemVectorFile = new java.io.PrintWriter(new java.io.File(itemVectorFileName))
                for((k,v) <- itemVectors){
                    modelFile.write("i;" + k)
                  for(value <- v){
                    itemVectorFile.write(k + ";" + value + '\n')
                    modelFile.write(";" + value)
                  }
                  modelFile.write("\n")
                }
                itemVectorFile.close()
                modelFile.close()
              }

            }).setParallelism(1)
